CREATE TABLE supplier (	id int NOT NULL AUTO_INCREMENT,	name varchar(50) NULL,	address varchar(100) NULL,	tel varchar(13) NULL,	coordnate_longitude varchar(50) NULL,	coordnate_latitude varchar(50) NULL,	businesstime_start varchar(50) NULL,	businesstime_end varchar(50) NULL,	delivery_time varchar(100) NULL,	create_date datetime NULL,	business_status smallint NULL,	PRIMARY KEY (id) );